CREATE TABLE indiewise_db.Critique
(
    id char(36) PRIMARY KEY NOT NULL,
    audio decimal(50,2),
    cinematography decimal(50,2),
    commentCount decimal(18,8) DEFAULT '0.00000000',
    createdAt datetime,
    direction decimal(50,2),
    editedAt datetime,
    music decimal(50,2),
    originality decimal(50,2),
    overall decimal(50,2),
    pacing decimal(50,2),
    performances decimal(50,2),
    private bit(1),
    production decimal(50,2),
    structure decimal(50,2),
    updatedAt datetime,
    writing decimal(50,2),
    project char(36),
    author char(36),
    url_id varchar(255),
    body text,
    CONSTRAINT project_project_bkname_critiques FOREIGN KEY (project) REFERENCES Project (id) ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT users_author_bkname_critiques FOREIGN KEY (author) REFERENCES users (id) ON DELETE CASCADE ON UPDATE CASCADE
);
CREATE UNIQUE INDEX critique_id_unique ON indiewise_db.Critique (id);
CREATE INDEX project_project_bkname_critiques ON indiewise_db.Critique (project);
CREATE INDEX users_author_bkname_critiques ON indiewise_db.Critique (author);
